import React from 'react';
import ChatBot from 'react-simple-chatbot';

const steps = [
  {
    id: '0',
    message: 'Escolha um país',
    trigger: '2'
  },
  {
    id: '2',
    options: [
      {value: 1, label: 'Brazil', trigger: '3'},
      {value: 2, label: 'Estados Unidos', trigger: '4'},
      {value: 3, label: 'Espanha', trigger: '5'},
      {value: 4, label: 'Inglaterra', trigger: '6'},
      {value: 5, label: 'França', trigger: '7'},
      {value: 6, label: 'Cuba', trigger: '8'},
      {value: 9, label: 'Itália', trigger: '9'}
    ]
  },
  {
    id: '3',
    message: 'Presidente Jair Bolsonaro',
    trigger: '0'
  },
  {
    id: '4',
    message: 'Presidente Donald Trump',
    trigger: '0'
  },
  {
    id: '5',
    message: 'Presidente do Governo Pedro Sánchez',
    trigger: '0'
  },
  {
    id: '6',
    message: 'Boris Johnson',
    trigger: '0'
  },
  {
    id: '7',
    message: 'Presidente Emmanuel Macron',
    trigger: '0'
  },
  {
    id: '8',
    message: 'Primeiro-ministro Manuel Marrero Cruz',
    trigger: '0'
  },
  {
    id: '9',
    message: 'Presidente Sergio Mattarella',
    trigger: '0'
  }
  
]

class Bot extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      titulo: 'Qual o nome do presidente ?'
    }
  }

  render() {
    return(
        <ChatBot 
            headerTitle={this.state.titulo} 
            steps={steps}
            style={{width: '100%', height: '100%'}}
            placeholder={'Não precisa digitar nada :)'}
            botBubbleColor={ '#E89984' } 
        />
    ); 
  }
}

export default Bot;
