import React from 'react';
import ChatBot from 'react-simple-chatbot';

const steps = [
  {
    id: '0',
    message: 'Bom dia',
    trigger: '2'
  },
  {
    id: '2',
    message: 'aaa'
  }

]

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      titulo: 'Qual o nome do presidente ?'
    }
  }

  render() {
    return(
      <ChatBot headerTitle={this.state.titulo} steps={steps}></ChatBot>
    ); 
  }
}

export default App;
