import React from 'react';
import ChatBot from 'react-simple-chatbot';

const steps = [
  {
    id: '0',
    message: 'Escolha um país',
    trigger: '2'
  },
  {
    id: '2',
    options: [
      {value: 1, label: 'Brazil', trigger: '3'},
      {value: 2, label: 'Argentina', trigger: '4'},
      {value: 3, label: 'Estados Unidos', trigger: '5'},
      {value: 4, label: 'Alemanha', trigger: '6'},
      {value: 5, label: 'Canadá', trigger: '7'},
      {value: 6, label: 'China', trigger: '8'},
      {value: 7, label: 'México', trigger: '9'},
    ]
  },
  {
    id: '3',
    message: 'aaaa'
  }

]

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      titulo: 'Qual o nome do presidente ?'
    }
  }

  render() {
    return(
      <ChatBot headerTitle={this.state.titulo} steps={steps}></ChatBot>
    ); 
  }
}

export default App;
