import React from 'react';
import ChatBot from 'react-simple-chatbot';

const steps = [
  {
    id: '0',
    message: 'Escolha um país',
    trigger: '2'
  },
  {
    id: '2',
    options: [
      {value: 1, label: 'Brazil', trigger: '3'},
      {value: 2, label: 'Estados Unidos', trigger: '4'},
    ]
  },
  {
    id: '3',
    message: 'Presidente Jair Bolsonaro',
    trigger: '0'
  },
  {
    id: '4',
    message: 'Presidente Donald Trump',
    trigger: '0'
  }

]

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      titulo: 'Qual o nome do presidente ?'
    }
  }

  render() {
    return(
      <ChatBot headerTitle={this.state.titulo} steps={steps}></ChatBot>
    ); 
  }
}

export default App;
